import React, { useState } from 'react';

const FileUpload = ({ onResults }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.type === 'audio/mpeg' || file.type === 'audio/mp3') {
        setSelectedFile(file);
        setError(null);
      } else {
        setError('Please upload an MP3 file.');
        setSelectedFile(null);
      }
    }
  };

  const handleFileUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await fetch('http://127.0.0.1:5000/upload', {
          method: 'POST',
          body: formData,
        });
        if (response.ok) {
          const result = await response.json();
          alert('File uploaded successfully!');
          onResults(result.timestamps || []);  // Safely pass timestamps to the parent component
        } else {
          const errorText = await response.json();
          alert(`File upload failed: ${errorText.error}`);
        }
      } catch (error) {
        console.error('Error uploading file:', error);
        alert('An error occurred during file upload.');
      }
    } else {
      alert('No file selected.');
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-2xl font-semibold mb-4 text-gray-800">Upload MP3 File</h2>
      <input
        type="file"
        accept=".mp3"
        onChange={handleFileChange}
        className="block w-full text-sm text-gray-700 file:py-2 file:px-4 file:border file:rounded-md file:text-sm file:font-semibold file:bg-gray-200 file:text-gray-700 hover:file:bg-gray-300"
      />
      {error && <p className="mt-2 text-red-500 text-sm">{error}</p>}
      <button
        onClick={handleFileUpload}
        className="mt-4 w-full px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
      >
        Upload MP3
      </button>
    </div>
  );
};

export default FileUpload;
