import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import VadResults from './components/VadResults';

function App() {
    const [timestamps, setTimestamps] = useState([]);
    const [uploadedFilePath, setUploadedFilePath] = useState('');

    const handleResults = (results, filePath) => {
        setTimestamps(results);
        setUploadedFilePath(filePath);
    };

    return (
        <div className="App">
            <h1>Voice Activity Detection</h1>
            <FileUpload onResults={handleResults} />
            {timestamps.length > 0 && 
                <VadResults timestamps={timestamps} filePath={uploadedFilePath} />}
        </div>
    );
}

export default App;
