import React, { useEffect, useRef } from 'react';
import WaveSurfer from 'wavesurfer.js';

const VadResults = ({ timestamps }) => {
    const waveformRef = useRef(null);
    const wavesurfer = useRef(null);

    useEffect(() => {
        // Initialize wavesurfer
        wavesurfer.current = WaveSurfer.create({
            container: waveformRef.current,
            waveColor: '#d9dcff',
            progressColor: '#4353ff',
            cursorColor: '#4353ff',
            backend: 'MediaElement',
        });

        // Load the audio file from the URL
        wavesurfer.current.load('/path-to-your-audio-file.mp3'); // Replace with the actual path

        // Cleanup wavesurfer on unmount
        return () => {
            if (wavesurfer.current) {
                wavesurfer.current.destroy();
            }
        };
    }, []);

    useEffect(() => {
        if (wavesurfer.current && timestamps.length > 0) {
            timestamps.forEach(([start, end]) => {
                wavesurfer.current.addRegion({
                    start: start / 1000,  // Convert ms to seconds
                    end: end / 1000,      // Convert ms to seconds
                    color: 'rgba(0, 123, 255, 0.3)',
                });
            });
        }
    }, [timestamps]);

    return (
        <div>
            <h2>Waveform</h2>
            <div ref={waveformRef}></div>
        </div>
    );
};

export default VadResults;
