import React, { useState } from 'react';

const FileUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Check if the file is an MP3
      if (file.type === 'audio/mpeg' || file.type === 'audio/mp3' || file.type === 'audio/MPEG' || file.type === 'audio/MP3') {
        setSelectedFile(file);
        setError(null);
      } else {
        setError('Please upload an MP3 file.');
        setSelectedFile(null);
      }
    }
  };

  const handleFileUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await fetch('http://127.0.0.1:5000/upload', {  // Adjust the URL to your Flask server's port
          method: 'POST',
          body: formData,
        });

        if (response.ok) {
          const result = await response.json();
          alert(result.message);
        } else {
          const errorText = await response.json();
          alert(`File upload failed: ${errorText.error}`);
        }
      } catch (error) {
        console.error('Error uploading file:', error);
        alert('An error occurred during file upload.');
      }
    } else {
      alert('No file selected.');
    }
  };

  return (
    <div className="file-upload-container">
      <input
        type="file"
        accept=".mp3"
        onChange={handleFileChange}
        className="file-input"
      />
      {error && <p className="error-message">{error}</p>}
      <button onClick={handleFileUpload} className="upload-button">
        Upload MP3
      </button>
    </div>
  );
};

export default FileUpload;
