const FileUpload = ({ onResults }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.type === 'audio/mpeg' || file.type === 'audio/mp3') {
        setSelectedFile(file);
        setError(null);
      } else {
        setError('Please upload an MP3 file.');
        setSelectedFile(null);
      }
    }
  };

  const handleFileUpload = async () => {
    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await fetch('http://127.0.0.1:5000/upload', {
          method: 'POST',
          body: formData,
        });
        alert('File uploaded successfully!');
        // Uncomment this block when backend integration is ready
        // if (response.ok) {
        //   alert('File uploaded successfully!');
        //   const result = await response.json();
        //   onResults(result.timestamps || []);  // Safely pass timestamps to the parent component
        // } else {
        //   const errorText = await response.json();
        //   alert(File upload failed: ${errorText.error});
        // }
      } catch (error) {
        console.error('Error uploading file:', error);
        alert('An error occurred during file upload.');
      }
    } else {
      alert('No file selected.');
    }
  };

  return (
    <div className="flex min-h-screen">
      {/* Left Side - Visual Demonstration */}
      <div className="w-1/2 bg-gray-200 p-6 flex items-center justify-center">
        <div className="max-w-md mx-auto">
          {/* Replace the following with your project visual */}
          <h1 className="text-3xl font-bold text-gray-800 text-center mb-4">Project Visual</h1>
          <p className="text-gray-700 text-center">
            Here you can showcase a visual representation of your project, such as a demo video, an interactive element, or any other relevant content.
          </p>
        </div>
      </div>

      {/* Right Side - File Upload Form */}
      <div className="w-1/2 p-6 flex items-center justify-center bg-white shadow-lg">
        <div className="max-w-md w-full">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Upload MP3 File</h2>
          <div className="mb-4">
            <input
              type="file"
              accept=".mp3"
              onChange={handleFileChange}
              className="block w-full text-sm text-gray-600 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100 focus:outline-none"
            />
            {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
          </div>
          <button
            onClick={handleFileUpload}
            className="w-full py-2 px-4 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-400 focus:ring-opacity-75"
          >
            Upload MP3
          </button>
          {selectedFile && (
            <p className="mt-4 text-gray-700 text-sm">
              Selected file: <span className="font-medium">{selectedFile.name}</span>
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default FileUpload;
