import React, { useState } from 'react';

function FileUpload({ onResults }) {
    const [file, setFile] = useState(null);

    const handleFileChange = (event) => {
        const uploadedFile = event.target.files[0];
        if (uploadedFile && uploadedFile.type === 'audio/mp3') {
            setFile(uploadedFile);
            console.log('File selected:', uploadedFile); // Log file to verify
        } else {
            alert('Please upload an MP3 file.');
        }
    };

    const handleFileUpload = () => {
        if (file) {
            console.log('Processing file:', file); // Log file to verify
            // Simulated processing
            const simulatedTimestamps = [
                { start: 0, end: 10 },
                { start: 15, end: 30 }
            ];
            onResults(simulatedTimestamps);
        } else {
            alert('No file selected.');
        }
    };

    return (
        <div className="file-upload">
            <input 
                type="file" 
                accept="audio/mp3" 
                onChange={handleFileChange} 
            />
            <button onClick={handleFileUpload}>Upload and Process</button>
        </div>
    );
}

export default FileUpload;
